mapper.update:
update t_agent_apply
SET status=#{status,}
WHERE id=#{id}

mapper.select:
select a.id id, a.stock_id holder, a.stock_name holderName, a.username account, a.realname realname, a.telephone telephone, a.email email, a.`status` status, a.resource_url source, a.register_ip registerIp, r.oper_user_name operUserName, r.create_time operatorTime from t_agent_apply a LEFT JOIN t_agent_review r ON(a.id=r.agent_apply_id)
WHERE a.owner_id=#{ownerId}
and a.status=#{status}
and a.username=#{account}
limit #{offset},#{limit}

select count(id) from t_agent_apply

 WHERE
  owner_id=#{ownerId}


    and status=#{status}


    and username=#{account}
